import logo from './logo.svg';
import './App.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import {AnimationOnScroll} from 'react-animation-on-scroll';
import 'animate.css/animate.min.css';

class NavBar extends React.Component {
  render() {
    return (
      <ul id="navBar">
          <a href="#resume"><li>Resume</li></a>
          <a href="#myProjects"><li>Projects</li></a>
          <a href="#mySkills"><li>My Skills</li></a>
          <a href="#aboutMe"><li>About Me</li></a>
      </ul>
    );
  } 
}

class Welcome extends React.Component {
  render() {
    return (
      <ul id="welcome">
          <h1>Mark Sandland</h1>
          <h2>Computer Science Graduate</h2>
          <h3>
            Hey, welcome to my website!
            <br/> 
            Please feel free to explore and view some of my work from University, as well as some from personal projects (such as this website :D)
          </h3>
          <a href="https://www.linkedin.com/in/mark-sandland/" target="_blank" id="linkedin" alt="linked in"/>
          <a href="https://codepen.io/marksandland" target="_blank" id="codepen" alt="codepen"/>
          <a href="https://github.com/marksandland" target="_blank" id="github" alt="github"/>
          <a href="mailto:marksandland11@gmail.com" target="_blank" id="email" alt="email"/>
      </ul>
    );
  }
}

class Projects extends React.Component {
  render() {
    return (

        <ul id="projects">
            <h1>Some of my work...</h1>
            <h2>Project Management Simulation Game</h2>
            <h3>
              This work was completed for my third year university project in which I was awarded a 1st!<br/> 
              The game is intended to be a fun way for players to gain an experience of what it is like to be a project manager in the software development industry.<br/>

            </h3>
            <AnimationOnScroll animateIn="animate__tada">
            <h2>Look what i am doing</h2>
            </AnimationOnScroll>
            <a href="https://www.linkedin.com/in/mark-sandland/" target="_blank" id="linkedin" alt="linked in"/>
            <a href="https://codepen.io/marksandland" target="_blank" id="codepen" alt="codepen"/>
            <a href="https://github.com/marksandland" target="_blank" id="github" alt="github"/>
            <a href="mailto:marksandland11@gmail.com" target="_blank" id="email" alt="email"/>

        </ul>



    );
  }
}

class Main extends React.Component {
  render() {
    return (
      <div id="main">
        <Welcome />
        <Projects />
      </div>
    );
  } 
}

class Wrapper extends React.Component {
  render() {
    return (
      <div id="wrapper">
        <NavBar />
        <Main />
      </div>
    )
  }
}

class App extends React.Component {
  componentDidMount() {
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            document.querySelector(this.getAttribute('href')).scrollIntoView({
                behavior: 'smooth'
            });
        });
    });
}
render(){
    return (
        <Wrapper />
    )
}
}

export default App;
