import logo from './logo.svg';
import './App.css';
import React, {useEffect} from 'react';
import ReactDOM from 'react-dom/client';
import Fade from 'react-reveal/Fade';
import Slide from 'react-reveal/Slide';
import 'animate.css';

class NavBar extends React.Component {


  toggleMenuVisibility = () => {
    var menuContainer = document.getElementById("menuContainer");
    var currentValue = menuContainer.style.visibility;

    if (currentValue != "visible"){
      menuContainer.style.visibility = "visible";
    }
    else {
      menuContainer.style.visibility = "hidden";
    }

  }

  render() {

    if (isMobile){
      return (
        <div>
          <ul id="navBar">
            <button id="menu" onClick={() => this.toggleMenuVisibility()}></button>
          </ul>
          <div id="menuContainer">
            <div id="menuDropdownBackdrop" onClick={() => this.toggleMenuVisibility()}/>
            <div id="menuDropdown">
              <a href="#welcome">Welcome</a>
              <a href="#projects">Projects</a>
              <a href="#resume">Resum&eacute;</a>
            </div>
          </div>
        </div>
      );
    }
    else {
      return (
        <ul id="navBar">
          <a href="#resume"><li>Resum&eacute;</li></a>
          <a href="#projects"><li>Projects</li></a>
          <a href="#welcome"><li>Welcome</li></a>
        </ul>
      );
    }
  } 
}

class Welcome extends React.Component {
  render() {
    return (
      <div id="welcome">
          <h1>Mark Sandland</h1>
          <h2>Computer Science Graduate</h2>
          <h3>
            Hello and welcome to my website!
            <br/> 
            Please feel free to explore and view some of my work from University, as well as some from personal projects (such as this website :D)
          </h3>
          <a href="https://www.linkedin.com/in/mark-sandland/" target="_blank" id="linkedin" alt="linked in"/>
          <a href="https://codepen.io/marksandland" target="_blank" id="codepen" alt="codepen"/>
          <a href="https://github.com/marksandland" target="_blank" id="github" alt="github"/>
          <a href="mailto:marksandland11@gmail.com" target="_blank" id="email" alt="email"/>
      </div>
    );
  }
}

class Projects extends React.Component {
  render() {
    return (
        <div id="projects">
            <Fade duration={1000}>
              <Slide top duration={1000}>
                <h1>Some of my work</h1>
              </Slide>
            </Fade>

            <Fade delay={500} duration={5000}>
                <div>
                  <h2>Project Management Simulation Game - Unity Game</h2>
                  <h4>
                    This work was completed for my third year university project in which I was awarded a 1st!<br/> 
                    The game is intended to be a fun way for players to gain an experience of what it is like to be a project manager in the software development industry.<br/>
                  </h4>
                </div>
            </Fade>

            <Fade delay={1000} duration={5000}>
                <div>
                  <h2>Amongst the Stars - Unity Game</h2>
                  <h4>
                    This is a game I was working on a while ago.<br/> 
                    The game is called "Amongst the Stars", and is a platformer game featuring levels to complete, enemies to defeat using the hero's sword and gun, upgrades, outfits and an endless scroller mode. Future plans for the game include a revision to the melee combat and improved level design.<br/>
                  </h4>
                </div>
            </Fade>

            <Fade delay={1500} duration={5000}>
                <div>
                  <h2>Online Diary Prototype</h2>
                  <h4>
                    This is a prototype I made for an online diary web app.<br/> 
                    The app is created with pure vanilla JS, HTML and CSS.<br/>
                  </h4>
                </div>
            </Fade>

            <Fade delay={2000} duration={5000}>
                <div>
                  <h2>Personal Website</h2>
                  <h4>
                    This is the website you are on right now!.<br/> 
                    I began this around the time of my graduation as a fun project to practise my front-end development skills and showcase some of my own personal projects.
                    I used react.js for this website which I learned from scratch during development. The intention for this website is for it to be an indefinite work in progress as I plan to improve it over time and add some future projects.<br/>
                  </h4>
                </div>
            </Fade>
        </div>
    );
  }
}

class Resume extends React.Component {
  render() {
    return (
        <div id="resume">
            <Fade duration={1000}>
              <Slide top duration={1000}>
                <h1>Resum&eacute;</h1>
              </Slide>
            </Fade>

            <Fade delay={500} duration={5000}>
                <div>
                  <a>Click here to download a copy of my CV</a>
                </div>
            </Fade>
        </div>
    );
  }
}

class Main extends React.Component {
  render() {
    return (
      <div id="main">
        <Welcome />
        <Projects />
        <Resume />
      </div>
    );
  } 
}

class Wrapper extends React.Component {
  render() {
    return (
      <div id="wrapper">
        <NavBar isMobile={this.props.isMobile}/>
        <Main />
      </div>
    )
  }
}

class App extends React.Component {
  componentDidMount() {
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            document.querySelector(this.getAttribute('href')).scrollIntoView({
                behavior: 'smooth'
            });
        });
    });
}

constructor() {
  super();
  this.state = {
    width: window.innerWidth,
  };
}

componentWillMount() {
window.addEventListener('resize', this.handleWindowSizeChange);
}

componentWillUnmount() {
  window.removeEventListener('resize', this.handleWindowSizeChange);
}

handleWindowSizeChange = () => {
  this.setState({ width: window.innerWidth });
};

  render(){
    const { width } = this.state;
    const isMobile = width <= 550;
      return (
          <Wrapper isMobile/>
      )
  }
}

export default App;
